config.siddhiApp:
@App:name('APIM_ERROR_SUMMARY')
define stream ErrorSummaryStream (apiName string, apiVersion string, apiResourceTemplate string, apiMethod string, apiCreator string,
apiCreatorTenantDomain string, applicationId string, applicationName string, applicationOwner string, responseCode int,
successCount int, faultCount int, throttledCount int, timestamp long);  @primaryKey('API_ID')  @store(type="rdbms" , datasource= "AM_DB")  define table AM_API(API_ID int,API_PROVIDER string,API_NAME string,API_VERSION string,CONTEXT string,CONTEXT_TEMPLATE string,API_TIER string,CREATED_BY string,CREATED_TIME string,UPDATED_BY string,UPDATED_TIME string);   @primaryKey('URL_MAPPING_ID') @store(type= "rdbms" , datasource= "AM_DB")  define table AM_API_URL_MAPPING(URL_MAPPING_ID int,API_ID int,HTTP_METHOD string,AUTH_SCHEME string,URL_PATTERN string,THROTTLING_TIER string,MEDIATION_SCRIPT string); @primaryKey('APPLICATION_ID') @store(type= "rdbms" , datasource= "AM_DB")  define table AM_APPLICATION(APPLICATION_ID int, NAME string, SUBSCRIBER_ID int, APPLICATION_TIER string, CALLBACK_URL string, DESCRIPTION string, APPLICATION_STATUS  string, GROUP_ID string, CREATED_BY string, CREATED_TIME string, UPDATED_BY string, UPDATED_TIME string, UUID string, TOKEN_TYPE string); @store(type = 'rdbms', datasource = 'APIM_ANALYTICS_DB',field.length =  "apiMethod:40 ") define aggregation ApiErrorAnalysisAgg  from ErrorSummaryStream  select apiName, apiVersion, apiMethod, apiResourceTemplate, applicationName, applicationOwner, responseCode, ifThenElse((responseCode/100) == 4,1,0) as _4xx, ifThenElse((responseCode/100) == 5,1,0) as _5xx, sum(successCount) as successCount, sum(faultCount) as faultCount, sum(throttledCount) as throttledCount, apiCreator, apiCreatorTenantDomain  group by apiName, apiVersion, responseCode, apiResourceTemplate, applicationId aggregate by timestamp every seconds...years;
